     1→import type { TakeFunction, TakeResult } from "../types";
     2→import type { RuntimeType } from "../runtime-types";
     3→import { unset } from "inferred-types/runtime";
     4→import { CONTAINER_PREFIXES, OPERATORS, trimToken, extractBetweenDelimiters, splitRespectingNesting } from "../utils";
     5→import { containerRuntimeType } from "../runtime-types/container";
     6→
     7→// Parser interface to avoid circular imports
     8→interface TokenParser {
     9→  parse(token: string): RuntimeType;
    10→}
    11→
    12→// This will be set by the main parser
    13→let parser: TokenParser | undefined;
    14→
    15→export function setContainerParser(p: TokenParser) {
    16→  parser = p;
    17→}
    18→
    19→/**
    20→ * Take function for Array<T> tokens
    21→ */
    22→export const takeArray: TakeFunction = (token: string): TakeResult | typeof unset => {
    23→  const trimmed = trimToken(token);
    24→  
    25→  
    26→  if (!trimmed.startsWith(CONTAINER_PREFIXES.ARRAY)) {
    27→    return unset;
    28→  }
    29→  
    30→  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.ARRAY, OPERATORS.ARRAY_END);
    31→  if (!extracted) {
    32→    return unset;
    33→  }
    34→  
    35→  let elementType;
    36→  if (extracted.content.trim()) {
    37→    try {
    38→      if (!parser) throw new Error("Parser not initialized");
    39→      elementType = parser.parse(extracted.content.trim());
    40→    } catch {
    41→      return unset;
    42→    }
    43→  }
    44→  
    45→  return {
    46→    type: containerRuntimeType.array(elementType),
    47→    remaining: extracted.remaining
    48→  };
    49→};
    50→
    51→/**
    52→ * Take function for Set<T> tokens
    53→ */
    54→export const takeSet: TakeFunction = (token: string): TakeResult | typeof unset => {
    55→  const trimmed = trimToken(token);
    56→  
    57→  if (!trimmed.startsWith(CONTAINER_PREFIXES.SET)) {
    58→    return unset;
    59→  }
    60→  
    61→  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.SET, OPERATORS.ARRAY_END);
    62→  if (!extracted) {
    63→    return unset;
    64→  }
    65→  
    66→  let elementType;
    67→  if (extracted.content.trim()) {
    68→    try {
    69→      if (!parser) throw new Error("Parser not initialized");
    70→      elementType = parser.parse(extracted.content.trim());
    71→    } catch {
    72→      return unset;
    73→    }
    74→  }
    75→  
    76→  return {
    77→    type: containerRuntimeType.set(elementType),
    78→    remaining: extracted.remaining
    79→  };
    80→};
    81→
    82→/**
    83→ * Take function for Map<K,V> tokens
    84→ */
    85→export const takeMap: TakeFunction = (token: string): TakeResult | typeof unset => {
    86→  const trimmed = trimToken(token);
    87→  
    88→  if (!trimmed.startsWith(CONTAINER_PREFIXES.MAP)) {
    89→    return unset;
    90→  }
    91→  
    92→  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.MAP, OPERATORS.ARRAY_END);
    93→  if (!extracted) {
    94→    return unset;
    95→  }
    96→  
    97→  // Parse key and value types separated by comma
    98→  const parts = splitRespectingNesting(extracted.content, OPERATORS.SEPARATOR);
    99→  if (parts.length !== 2) {
   100→    return unset;
   101→  }
   102→  
   103→  let keyType, valueType;
   104→  try {
   105→    if (!parser) throw new Error("Parser not initialized");
   106→    keyType = parser.parse(parts[0].trim());
   107→    valueType = parser.parse(parts[1].trim());
   108→  } catch {
   109→    return unset;
   110→  }
   111→  
   112→  return {
   113→    type: containerRuntimeType.map(keyType, valueType),
   114→    remaining: extracted.remaining
   115→  };
   116→};
   117→
   118→/**
   119→ * Take function for WeakMap<K,V> tokens
   120→ */
   121→export const takeWeakMap: TakeFunction = (token: string): TakeResult | typeof unset => {
   122→  const trimmed = trimToken(token);
   123→  
   124→  if (!trimmed.startsWith(CONTAINER_PREFIXES.WEAKMAP)) {
   125→    return unset;
   126→  }
   127→  
   128→  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.WEAKMAP, OPERATORS.ARRAY_END);
   129→  if (!extracted) {
   130→    return unset;
   131→  }
   132→  
   133→  // Parse key and value types separated by comma
   134→  const parts = splitRespectingNesting(extracted.content, OPERATORS.SEPARATOR);
   135→  if (parts.length !== 2) {
   136→    return unset;
   137→  }
   138→  
   139→  let keyType, valueType;
   140→  try {
   141→    if (!parser) throw new Error("Parser not initialized");
   142→    keyType = parser.parse(parts[0].trim());
   143→    valueType = parser.parse(parts[1].trim());
   144→  } catch {
   145→    return unset;
   146→  }
   147→  
   148→  return {
   149→    type: containerRuntimeType.weakMap(keyType, valueType),
   150→    remaining: extracted.remaining
   151→  };
   152→};
   153→
   154→/**
   155→ * Take function for Record<K,V> tokens
   156→ */
   157→export const takeRecord: TakeFunction = (token: string): TakeResult | typeof unset => {
   158→  const trimmed = trimToken(token);
   159→  
   160→  if (!trimmed.startsWith(CONTAINER_PREFIXES.RECORD)) {
   161→    return unset;
   162→  }
   163→  
   164→  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.RECORD, OPERATORS.ARRAY_END);
   165→  if (!extracted) {
   166→    return unset;
   167→  }
   168→  
   169→  // Parse key and value types separated by comma
   170→  const parts = splitRespectingNesting(extracted.content, OPERATORS.SEPARATOR);
   171→  if (parts.length !== 2) {
   172→    return unset;
   173→  }
   174→  
   175→  let keyType, valueType;
   176→  try {
   177→    if (!parser) throw new Error("Parser not initialized");
   178→    keyType = parser.parse(parts[0].trim());
   179→    valueType = parser.parse(parts[1].trim());
   180→  } catch {
   181→    return unset;
   182→  }
   183→  
   184→  return {
   185→    type: containerRuntimeType.record(keyType, valueType),
   186→    remaining: extracted.remaining
   187→  };
   188→};

