     1import type { TakeFunction, TakeResult } from "../types";
     2import { unset } from "inferred-types/runtime";
     3import { ATOMIC_TOKENS, trimToken } from "../utils";
     4import { atomicRuntimeType } from "../runtime-types/atomic";
     5
     6/**
     7 * Take function for atomic types: null, undefined, boolean, true, false, string, number, object, symbol
     8 */
     9export const takeAtomic: TakeFunction = (token: string): TakeResult | typeof unset => {
    10  const trimmed = trimToken(token);
    11  
    12  // Check for exact matches of atomic tokens
    13  if (trimmed === ATOMIC_TOKENS.NULL) {
    14    return {
    15      type: atomicRuntimeType.null(),
    16      remaining: ""
    17    };
    18  }
    19  
    20  if (trimmed === ATOMIC_TOKENS.UNDEFINED) {
    21    return {
    22      type: atomicRuntimeType.undefined(),
    23      remaining: ""
    24    };
    25  }
    26  
    27  if (trimmed === ATOMIC_TOKENS.TRUE) {
    28    return {
    29      type: atomicRuntimeType.true(),
    30      remaining: ""
    31    };
    32  }
    33  
    34  if (trimmed === ATOMIC_TOKENS.FALSE) {
    35    return {
    36      type: atomicRuntimeType.false(),
    37      remaining: ""
    38    };
    39  }
    40  
    41  if (trimmed === ATOMIC_TOKENS.BOOLEAN) {
    42    return {
    43      type: atomicRuntimeType.boolean(),
    44      remaining: ""
    45    };
    46  }
    47  
    48  if (trimmed === ATOMIC_TOKENS.STRING) {
    49    return {
    50      type: atomicRuntimeType.string(),
    51      remaining: ""
    52    };
    53  }
    54  
    55  if (trimmed === ATOMIC_TOKENS.NUMBER) {
    56    return {
    57      type: atomicRuntimeType.number(),
    58      remaining: ""
    59    };
    60  }
    61  
    62  if (trimmed === ATOMIC_TOKENS.OBJECT) {
    63    return {
    64      type: atomicRuntimeType.object(),
    65      remaining: ""
    66    };
    67  }
    68  
    69  if (trimmed === ATOMIC_TOKENS.SYMBOL) {
    70    return {
    71      type: atomicRuntimeType.symbol(),
    72      remaining: ""
    73    };
    74  }
    75  
    76  // Check for atomic tokens at the start of the string (for compound expressions)
    77  for (const [key, value] of Object.entries(ATOMIC_TOKENS)) {
    78    if (trimmed.startsWith(value)) {
    79      // Make sure it's a complete token (not part of a larger identifier)
    80      const nextChar = trimmed[value.length];
    81      if (!nextChar || /[\s|&(),<>\[\]{}]/.test(nextChar)) {
    82        const factory = atomicRuntimeType[key.toLowerCase() as keyof typeof atomicRuntimeType];
    83        if (factory) {
    84          return {
    85            type: factory(),
    86            remaining: trimmed.slice(value.length)
    87          };
    88        }
    89      }
    90    }
    91  }
    92  
    93  return unset;
    94};

