     1import type { TakeFunction, TakeResult } from "../types";
     2import type { RuntimeType } from "../runtime-types";
     3import { unset } from "inferred-types/runtime";
     4import { CONTAINER_PREFIXES, OPERATORS, trimToken, extractBetweenDelimiters, splitRespectingNesting } from "../utils";
     5import { containerRuntimeType } from "../runtime-types/container";
     6
     7// Parser interface to avoid circular imports
     8interface TokenParser {
     9  parse(token: string): RuntimeType;
    10}
    11
    12// This will be set by the main parser
    13let parser: TokenParser | undefined;
    14
    15export function setContainerParser(p: TokenParser) {
    16  parser = p;
    17}
    18
    19/**
    20 * Take function for Array<T> tokens
    21 */
    22export const takeArray: TakeFunction = (token: string): TakeResult | typeof unset => {
    23  const trimmed = trimToken(token);
    24  
    25  
    26  if (!trimmed.startsWith(CONTAINER_PREFIXES.ARRAY)) {
    27    return unset;
    28  }
    29  
    30  const extracted = extractBetweenDelimiters(trimmed, CONTAINER_PREFIXES.ARRAY, OPERATORS.ARRAY_END);
    31  if (!extracted) {
    32    return unset;
    33  }
    34  
    35  let elementType;
    36  if (extracted.content.trim()) {
    37    try {
    38      if (!parser) throw new Error("Parser not initialized");
    39      elementType = parser.parse(extracted.content.trim());
    40    } catch {
    41      return unset;
    42    }
    43  }
    44  
    45  return {
    46    type: containerRuntimeType.array(elementType),
    47    remaining: extracted.remaining
    48  };
    49};
    50
    51/**
    52 * Take function for Set<T> tokens
    53 */
